function select2DataCollectName(d) {
    if (d.children)
        d.children.forEach(select2DataCollectName);
    else if (d._children)
        d._children.forEach(select2DataCollectName);
    if (d.name.length > 1 && d.parent !== 'null' && d.parent !== 'ICDTree') {
        select2Data.push(d.ACRONYM);
        select2Data.push(d.Description);
        select2Data.push(d.EIR)
        select2Data.push(d.name);
    }
}

function searchTree(d) {
    if (d.children)
        d.children.forEach(searchTree);
    else if (d._children)
        d._children.forEach(searchTree);
    for (var i = 0; i < searchField.length; i++) {
        var searchFieldValue = d[searchField[i]];
        if (searchFieldValue && searchFieldValue.match(searchText) || searchFieldValue === searchText) {
                // Walk parent chain
                var ancestors = [];
                var parent = d;
                while (typeof(parent) !== "undefined") {
                    ancestors.push(parent);
                    parent.class = "found";
                    parent = parent.parent;
                }
        }
    }
}

function clearAll(d) {
    d.class = "";
    if (d.children)
        d.children.forEach(clearAll);
    else if (d._children)
        d._children.forEach(clearAll);
}

function collapse(d) {
    if (d.children) {
        d._children = d.children;
        d._children.forEach(collapse);
        d.children = null;
    }
}

function collapseAllNotFound(d) {
    if (d.children) {
    	if (d.class !== "found") {
        	d._children = d.children;
        	d._children.forEach(collapseAllNotFound);
        	d.children = null;
	} else
        	d.children.forEach(collapseAllNotFound);
    }
}

function expandAll(d) {
    if (d._children) {
        d.children = d._children;
        d.children.forEach(expandAll);
        d._children = null;
    } else if (d.children)
        d.children.forEach(expandAll);
}

// Toggle children on click.
function toggle(d) {
  if (d.children) {
    d._children = d.children;
    d.children = null;
  } else {
    d.children = d._children;
    d._children = null;
  }
  clearAll(root);
  update(d);
  $("#searchName").select2("val", "");
}

function dblclick(d) {
  window.location.assign(d.URL);
}

$("#searchName").on("select2-selecting", function(e) {
    clearAll(root);
    expandAll(root);
    update(root);

    searchField = ['ACRONYM', 'Description', 'EIR', 'name'];
    searchText = e.object.text;
    searchTree(root);
    root.children.forEach(collapseAllNotFound);
    update(root);
});

