root.x0 = height / 2;
root.y0 = 0;

select2Data = [];
select2DataCollectName(root);
select2DataObject = [];
select2Data.sort(function(a, b) {
        if (a > b) return 1; // sort
        if (a < b) return -1;
        return 0;
    });
select2Data = select2Data
    .filter(function(item, i, ar) {
        return ar.indexOf(item) === i;
    }); // remove duplicate items
select2Data.forEach(function(item, i, ar) {
    select2DataObject.push({
        "id": i,
        "text": item
    });
});

$("#searchName").select2({
    data: select2DataObject,
    containerCssClass: "search",
    placeholder: 'Search'
});

function collapse(d) {
    if (d.children) {
        d._children = d.children;
        d._children.forEach(collapse);
        d.children = null;
    }
}
root.children.forEach(collapse);
update(root);

d3.select(self.frameElement).style("height", "800px");

function update(source) {

  // Compute the new tree layout.
  var nodes = tree.nodes(root).reverse(),
      links = tree.links(nodes);

  // Normalize for fixed-depth.
  nodes.forEach(function(d) { d.y = d.depth * 180; });

  // Update the nodes�
  var node = svg.selectAll("g.node")
      .data(nodes, function(d) { return d.id || (d.id = ++i); });

  // Enter any new nodes at the parent's previous position.
  var nodeEnter = node.enter().append("g")
      .attr("class", "node")
      .attr("transform", function(d) { return "translate(" + source.y0 + "," + source.x0 + ")"; });

      nodeEnter.append("circle")
        .attr("r", 1e-6)
        .style("fill", function(d) { return d._children ? "lightsteelblue" : "#fff"; })
            .filter(function(d) { return d.children || d._children ? true : false; })
            .attr("class","actionable")
            .on("click", toggle)
            .append("title")
        .text(function(d) { return d._children ? "Expand" : "Collapse"; });

        nodeEnter.append("text")
            .attr("x", function(d) { return (d.children || d._children ? -1 : 1) * (d.URL ? 20 : 20) ; })
            .attr("dy", ".35em")
            .attr("text-anchor", function(d) { return d.children || d._children ? "end" : "start"; })
            .append("tspan")
            .text(function(d) { return d.name; })
            .filter(function(d) { return d.children || d._children ? true : false; })
            .attr("class","actionable")
            .on("click", toggle)
            .append("title")
            .text(function(d) { return d._children ? "Expand" : "Collapse"; });

        nodeEnter.append("image")
            .attr("x", function(d) { return d.children || d._children ? -22 : 3; })
            .attr("y", "-.75em")
            .attr("width", "20")
            .attr("height", "22")
            .attr("xlink:href", "images/link.png")
            .style("display", function (d) {
                if (d.URL) {
                    return 'inline-block'
                } else {
                    return 'none'
                }
            })
            .style("opacity", function (d) {return ".5"})
            .attr("class","actionable")
            .on("click", dblclick)
            .append("title")
            .text("Go to Policy Page");


  nodeEnter.append("text")
      .attr("x", function(d) { return d.children || d._children ? -20 : 20; })
      .attr("dy", ".70em")
      .attr("text-anchor", function(d) { return d.children || d._children ? "end" : "start"; })
      .text(function(d) { return d.name; })
      .style("fill-opacity", 1e-6);

  // Transition nodes to their new position.
  var nodeUpdate = node.transition()
      .duration(duration)
      .attr("transform", function(d) { return "translate(" + d.y + "," + d.x + ")"; });

  nodeUpdate.select("circle")
      .attr("r", 4.5)
      .style("fill", function(d) {
            if (d.class === "found") {
                return "#ff4136"; //red
            } else if (d._children) {
                return "lightsteelblue";
            } else {
                return "#fff";
            }
        })
        .style("stroke", function(d) {
            if (d.class === "found") {
                return "#ff4136"; //red
            }
        });

  nodeUpdate.select("text")
      .style("fill-opacity", 1);

  // Transition exiting nodes to the parent's new position.
  var nodeExit = node.exit().transition()
      .duration(duration)
      .attr("transform", function(d) { return "translate(" + source.y + "," + source.x + ")"; })
      .remove();

  nodeExit.select("circle")
      .attr("r", 1e-6);

  nodeExit.select("text")
      .style("fill-opacity", 1e-6);

  // Update the links�
  var link = svg.selectAll("path.link")
      .data(links, function(d) { return d.target.id; });

  // Enter any new links at the parent's previous position.
  link.enter().insert("path", "g")
      .attr("class", "link")
      .attr("d", function(d) {
        var o = {x: source.x0, y: source.y0};
        return diagonal({source: o, target: o});
      });

  // Transition links to their new position.
  link.transition()
      .duration(duration)
      .attr("d", diagonal)
      .style("stroke", function(d) {
            if (d.target.class === "found") {
                return "#ff4136";
            }
        });

  // Transition exiting nodes to the parent's new position.
  link.exit().transition()
      .duration(duration)
      .attr("d", function(d) {
        var o = {x: source.x, y: source.y};
        return diagonal({source: o, target: o});
      })
      .remove();

  // Stash the old positions for transition.
  nodes.forEach(function(d) {
    d.x0 = d.x;
    d.y0 = d.y;
  });
}
